-- Function to get category_id by name
CREATE OR REPLACE FUNCTION get_cat_id(cat_name TEXT) RETURNS UUID AS $$
BEGIN
    RETURN (SELECT id FROM public.categories WHERE name = cat_name LIMIT 1);
END;
$$ LANGUAGE plpgsql;

