-- Function to get item_id by name
CREATE OR REPLACE FUNCTION get_item_id(i_name TEXT) RETURNS UUID AS $$
BEGIN
    RETURN (SELECT id FROM public.menu_items WHERE name = i_name LIMIT 1);
END;
$$ LANGUAGE plpgsql;

